    private void Seleccion(){
       
    if (Radio1.isSelected()){
            Radio1.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Imagenes/imagen1.jpg")));
            Radio2.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Imagenes/imagen2.jpg")));
    } else if(Radio2.isSelected()){
            Radio2.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Imagenes/imagen1.jpg")));
            Radio1.setIcon(new javax.swing.ImageIcon(getClass().getResource("/Imagenes/imagen2.jpg")));
        }
    }
